      CASE WHEN (SELECT COUNT(DISTINCT salary) FROM Employee) > N
        THEN ( WITH CTE AS (SELECT SALARY, DENSE_RANK() OVER(ORDER BY SALARY DESC) 
           AS DENSERANK FROM EMPLOYEE) 
            SELECT SALARY FROM CTE WHERE DENSERANK = N
