    }

private:
    int min_turns_to_print(int start, int end, const std::string& s, std::unordered_map<std::string, int>& memo) {
        if (start > end) {
            return 0;
        }

        std::string key = std::to_string(start) + "-" + std::to_string(end);
        if (memo.count(key)) {
            return memo[key];
