class Solution {
    public int strangePrinter(String s) {
        int n=s.length();
        int[][] f=new int[n][n];
        for (int i=n-1;i>=0;i--) 
            for (int j=i;j<n;j++) 
            {
                f[i][j]=(i==j)?1:1+f[i+1][j];
                for (int k=i+1;k<=j;k++) 
