from functools import lru_cache

class Solution:
    def strangePrinter(self, s: str) -> int:
        @lru_cache(None)
        #min cost to print s[i:j+1]
        def dfs(i,j):
            if i > j:
                return 0
            #s[j] is printed with cost 1
            ans = dfs(i,j-1)+1
            #s[j] is printed with cost 0 due to a previous character that is same as s[j]
            #A...A...A
            #s[i]...s[j]...s[k]
            #in this case, if the optimal case prints all the "A" due to the leftmost "A",
            #then dfs(i,i)+d(i+1,j-1)+d(j+1,k-1) contributes to the answer
            #that is, cost 0 can be propagated to the left
            for k in range(i,j):
                if s[k] == s[j]:
                    ans = min(ans, dfs(i,k)+dfs(k+1,j-1))
            return ans
        return dfs(0,len(s)-1)

