class Solution:
    def strangePrinter(self, s: str) -> int:
        compressed_s = "".join([s[i] for i in range(len(s)) if i == 0 or s[i] != s[i-1]])

        memo = {}
        def solve(low: int, high: int, prev_printed_char: str) -> int:
            # Check to avoid indexing errors
            if low > high:
                return 0

            # Observation 4 and 5. Avoid wasting prints.
            if compressed_s[low] == prev_printed_char:
                low += 1
            if compressed_s[high] == prev_printed_char:
                high -= 1

            # Base cases and memoization.
            if low > high:
                return 0
            if low == high:
                return int(compressed_s[low] != prev_printed_char) 
            if (low, high, prev_printed_char) in memo:
                return memo[low, high, prev_printed_char]

            # Recursive case 
			# 100 chars requires at most 100 prints
            min_prints = 100
            for mid in range(low, high + 1):
                if compressed_s[low] == compressed_s[mid]:
					# min_prints = min(min_prints, 1 + solve(low, mid, compressed_s[low]) + solve(mid + 1, high, prev_printed_char))
                    min_prints = min(min_prints, 1 + solve(low + 1, mid - 1, compressed_s[low]) + solve(mid + 1, high, prev_printed_char))
            memo[low, high, prev_printed_char] = min_prints 
            return min_prints

        # didn't print anything previously. Use " " representing nothing being previously printed.
        return solve(0, len(compressed_s) - 1, " ")
