class Solution:
    def strangePrinter(self, s: str) -> int:
        compressed_s = "".join([s[i] for i in range(len(s)) if i == 0 or s[i] != s[i-1]])

		# Hopefully, this isn't too hard to understand. But previous_index[char] represents
		# the previous index where char was spotted. And for each compressed_s[j] (char), 
		# if compressed_s[j] (char) is in previous_index, previous_index[char] is the i described
		# above and j is the j described above.
        successor = [100 for _ in range(len(compressed_s))]
        previous_index = {}
        for j, char in enumerate(compressed_s):
            if char in previous_index:
                successor[previous_index[char]] = j 
            previous_index[char] = j

        memo = {}
        def solve(low: int, high: int, prev_printed_char: str) -> int:
            # Check to avoid indexing errors
            if low > high:
                return 0

            # Observation 4 and 5. Avoid wasting prints.
            if compressed_s[low] == prev_printed_char:
                low += 1
            if compressed_s[high] == prev_printed_char:
                high -= 1

            # Base cases and memoization.
            if low > high:
                return 0
            if low == high:
                return int(compressed_s[low] != prev_printed_char) 
            if (low, high, prev_printed_char) in memo:
                return memo[low, high, prev_printed_char]

            # Recursive case 
			min_prints = 100
            mid = low
            while mid <= high:
                min_prints = min(min_prints, 1 + solve(low + 1, mid - 1, compressed_s[low]) + solve(mid + 1, high, prev_printed_char))
                mid = successor[mid]
            memo[low, high, prev_printed_char] = min_prints 
            return min_prints

        # didn't print anything previously. Use " " representing nothing.
        return solve(0, len(compressed_s) - 1, " ")
