private:
    int solve(int i, int j, const string& s, vector<vector<int>>& dp) {
        if (i == j)
            return 1;

        if (dp[i][j] != -1)
            return dp[i][j];

        int mini = INT_MAX;

        for (int k = i; k < j; k++) {
            int steps = solve(i, k, s, dp) + solve(k + 1, j, s, dp);
            mini = min(steps, mini);
        }

        if (s[i] == s[j])
            mini--;

        return dp[i][j] = mini;
    }
};
