class Solution {
    func strangePrinter(_ s: String) -> Int {
        var memo: [String.Index: [String.Index: Int]] = [:]
        func minTurns(lo: String.Index, hi: String.Index) -> Int {
            if let cachedResult = memo[lo]?[hi] {
                return cachedResult
            }

            guard lo < hi else { return lo == hi ? 1 : 0 }
            var k = s.index(after: lo)
            if k == hi {
                let res = s[lo] == s[hi] ? 1 : 2
                memo[lo, default: [:]][hi] = res
                return res
