            var res = 1 + minTurns(lo: k, hi: hi)

            while k <= hi {
                if s[k] == s[lo] {
                    let left = minTurns(lo: lo, hi: s.index(before: k))
                    let right = minTurns(lo: s.index(after: k), hi: hi)
                    res = min(res, left + right)
