const int INF = 1e9;

class Solution {
public:
    int strangePrinter(string s) {
        int n = s.length();
        vector<vector<int>> dp(n, vector<int>(n, 0));

        for (int d = 0; d < n; ++d) {
            for (int idx = 0; idx < n; ++idx) {
                int l = idx, r = idx + d;
                if (r >= n) {
                    break;
                }
                dp[l][r] = INF;
                if (l == r) {
                    dp[l][r] = 0;
                } else if (s[l] != s[l + 1]) {
                    dp[l][r] = min(dp[l][r], dp[l + 1][r] + 1);
                    for (int i = l + 1; i <= r; ++i) {
                        if (s[l] == s[i]) {
                            dp[l][r] = min(dp[l][r], dp[l][i - 1] + dp[i][r]);
                        }
                    }
                } else {
                    dp[l][r] = dp[l + 1][r];
                }
            }
        }

        return dp[0][n - 1] + 1;
    }
};
