    def strangePrinter(self, s: str) -> int:
        char_map = collections.defaultdict(list)
        dedup = []
        prev = None
        pos = 0
        for char in s:
            if char != prev:
                char_map[char].append(pos)
                dedup.append(char)
                prev = char
                pos += 1

        @functools.cache
        def dp(i, k):
            if i >= k:
                return 1
            char = dedup[i]
            result = 1 + dp(i+1, k)
            for j in char_map[char]:
                if i < j <= k:
                    result = min(result, dp(i+1, j-1) + dp(j, k))
            return result

        n = len(dedup)
        return dp(0, n-1)
