class Solution {
public:

    int solve(int l,int r,string s,vector<vector<int>> &mem) {
        if(l > r) return 0;

        if(mem[l][r] != -1) return mem[l][r];

        // so now basically you have two choices for this interval l to r.
        // you can take any of it's end.(l or r) , i have taken r.
        // so choice 1: treat this s[r] as an individual character and count 1 turn for it.
        // choice 2: don't count turn for it, wait for characters which are before it and try to merge this s[r] with them.
        // so now for choice 2 the new intervals would be if s[i] == s[r] (l <= i < r)
        // then l to i (not i-1 bcz we want to merge i'th charater) and (i+1 to r-1)

        int res = 0;
        // choice 1 : dont want to merge
        res = 1 + solve(l,r-1,s,mem);

        // choice 2 : wanna wait and want to merge
        for(int i=l;i<r;i++) {
            if(s[i] == s[r])
                res = min(res,solve(l,i,s,mem)+solve(i+1,r-1,s,mem));
        }

        return mem[l][r] = res;
    }

    int strangePrinter(string s) {
        int n = s.length();

        vector<vector<int>> mem(n+1,vector<int>(n+1,-1));

        return solve(0,n-1,s,mem);
    }
};
