public int StrangePrinter(string s)
{
	int n = s.Length;
	if (n == 0)
		return 0;

	// dp[i,j] is the minimum number of turns needed to print s[i..j]
	int[,] dp = new int[n,n];
	for (int i = 0; i < n; i++)
		dp[i,i] = 1;

	for (int len = 2; len <= n; len++)
	{
		for (int i = 0; i + len - 1 < n; i++)
		{
			int j = i + len - 1;
			dp[i,j] = dp[i,j - 1] + 1;
			for (int k = i; k < j; k++)
			{
				if (s[k] == s[j])
					dp[i,j] = Math.Min(dp[i,j], dp[i,k] + dp[k + 1,j - 1]);
			}
		}
	}

	return dp[0,n - 1];
}

