    int strangePrinter(string s) {
        s.erase(unique(s.begin(), s.end()), s.end());
        int n = s.length();

        int dp[n][n+1];
        memset(dp,0,sizeof(dp));
        for(int i = 0;i<n;i++)
            dp[i][i+1] = 1;

        for(int k = 2;k<=n;k++)
        {
            for(int l = 0;l<=(n-k);l++)
            {
                int r = l+k;
                dp[l][r] = INT_MAX;

                if(s[l]==s[r-1])
