                    dp[l][r] = min(dp[l][r],dp[l][m]+dp[m][r]);

                    if(s[l]==s[m]&&s[m]==s[r-1])
                        dp[l][r] = min(dp[l][r],dp[l][m]+dp[m][r]-1);
                }
