class Solution {
public:

    int n;

    vector<vector<int>> dp;

    int fun(int l, int h, string& s){
        if(l == h)                                   // single element
            return 1;
        else if(l > h)                          // no string
            return 0;
        if(dp[l][h] != -1)                     // if already solved, return
            return dp[l][h];


        int i = l+1;
        while(i <= h && s[i] == s[l])i++;                 // get the first index with different character

        if(i == h+1)                                 // if all characters are same, we reach end of the string
            return 1;                                // hence can be printed in 1 move

        int normal = 1 + fun(i,h,s);              // print current same characters in one step and get answer for remaining string

        int continue = INT_MAX;               // continue the streak, with possible same characters

        for(int j = i; j <= h; j++){
            if(s[l] == s[j]){
                int x = fun(i,j-1,s) + fun(j,h,s);
                cont = min(cont, x);
            }
        }

        return dp[l][h] = min(cont,normal);              // memoize and return minimum of both options
    }

    int strangePrinter(string s) {
        n = s.length() ;
        dp = vector<vector<int>>(n,vector<int>(n, -1));
        return fun(0, n-1, s);
    }
};

