class Solution {
    int dp[][][];
    boolean visit[][][];
    public int strangePrinter(String s) {
        int n=s.length();
        visit=new boolean[n][n][n];
        dp=new int[n][n][n];
        int nums[]=new int[n];
        for(int i=0;i<s.length();i++)nums[i]=s.charAt(i)-'a';
        return dfs(nums,0,nums.length-1,0);
    }

    public int dfs(int nums[],int left,int right,int n){
        if(left>right)return 0;
        if(left==right){
            return (1);
        }
        int min=Integer.MAX_VALUE;
        for(int i=left+1;i<=right;i++){
            if(nums[i]==nums[left]){
                if(!visit[i][right][1+n]){
                    dp[i][right][1+n]=dfs(nums,i,right,1+n);
                    visit[i][right][1+n]=true;
                }
                if(!visit[left+1][i-1][0]){
                    dp[left+1][i-1][0]=dfs(nums,left+1,i-1,0);
                    visit[left+1][i-1][0]=true;
                }
                min=Math.min(min,dp[i][right][1+n]+dp[left+1][i-1][0]);
            }
        }
        if(!visit[left+1][right][0]){
            dp[left+1][right][0]=dfs(nums,left+1,right,0);
            visit[left+1][right][0]=true;
        }
        min=Math.min(min,(1)+dp[left+1][right][0]);
        return min;
    }
}
