/*



*/


class Solution {

    int[][] cache;

    public int strangePrinter(String s) {
        cache = new int[s.length()][s.length()];
        for(int[] row : cache){
            Arrays.fill(row, -1);
        }
        return getMinCostRecursive(s, 0, s.length()-1);       
    }

    public int getMinCostRecursive(String s, int left, int right) {


        if(left > right) {
            return 0;
        }

        if(cache[left][right] != -1) {
            return cache[left][right];
        }


        int ch = s.charAt(right);

        // with cost of 1
        int minCost = 1 + getMinCostRecursive(s, left, right - 1);

        for(int i = left; i < right; i++) {
            if(s.charAt(i) == ch) {
                // piggy back on previous cost 
                minCost = Math.min(minCost, getMinCostRecursive(s, left, i) + getMinCostRecursive(s,i + 1, right-1));
            }
        }

        cache[left][right] = minCost;

        return minCost;

    }

}
