    int solve(int i, int j, string& s) {
        if(i == j) return 1;
        if(dp[i][j] != -1) return dp[i][j];
        int out = INT_MAX;
        for(int k=i;k<j;k++) {
            out = min(out, solve(i, k, s)+solve(k+1, j, s));
