# Code
```
class Solution {
public:
    int helper(int l, int r, string& s,vector<vector<int>>&dp){ 
        if (l == r) return 1;   
        if(l > r) return 0;   
        if (dp[l][r]!=-1) return dp[l][r];

        int i = l+1;
        while(i<=r && s[i]==s[l]) i++;

        if(i == r+1) return 1;

        int basic = 1 + helper(i,r,s,dp);
        int greedy = INT_MAX;

        for(int j=i;j<=r;j++){
            if(s[j] == s[l]){
                int res = helper(i,j-1,s,dp) + helper(j,r,s,dp);
                greedy = min(greedy,res);
            }
        }

        return dp[l][r] = min(basic,greedy);
    }

    int strangePrinter(string s) {
        int n=s.size();
        vector<vector<int>>dp(n,vector<int>(n,-1));
        return helper(0, n-1,s,dp);
    }
};
```

