```
class Solution {
public:
    int f(int i, int j, string& s, vector<vector<int>>& dp){       
        if (dp[i][j]) return dp[i][j];
        if (i==j) return dp[i][j]=1;
        int minPrint = 0;
        if (s[i]==s[j]) minPrint=f(i, j-1, s, dp);
        else{
            minPrint=f(i,j-1, s, dp)+1;
            for(int k=i+1; k<j; k++){
                if(s[k]==s[j])
                    minPrint=min(minPrint, f(i, k-1, s, dp)+f(k, j-1, s, dp));
            }
        }
