class Solution:

    def strangePrinter(self, s: str) -> int:
        # Get rid of duplicates and convert the string to a list for easy indexing
        l = [s[0]]
        for c in s:
            if c != l[-1]: l.append(c)
        s = l

        n = len(s)
        memo = [[0] * n for _ in range(n)]

        # The new function for dynamic programming with memoization
        def dp(l, r):
            if l > r:
                return 0
            if memo[l][r] != 0:
                return memo[l][r]

            ans = 1 + dp(l + 1, r)  # Start by printing the current character

            for i in range(l + 1, r + 1):
                if s[l] == s[i]:
                    ans = min(ans, dp(l, i - 1) + dp(i + 1, r))

            memo[l][r] = ans
            return ans

        return dp(0, len(s) - 1)

