    int strangePrinter(string s) {
        int n = s.size();
        vector<vector<int>> m(n,vector<int>(n,1));
        for(int i = n-1;i>=0;i--){
            for(int d=1;d<n-i;d++){
                int j = i+d;
                if(d==1){
                    if(s[i]==s[j]){
                        m[i][j] = 1;
