                m[i][j] = INT_MAX;
                for(int k=i;k<j;k++){
                    m[i][j] = min(m[i][k]+m[k+1][j],m[i][j]);
                }
                if(s[i]==s[j]){
                    m[i][j] -= 1;
                }
            }
        }
        return m[0][n-1];
    }
};
