class Solution {
public:
    string addBinary(string a, string b) {
        string result = "";
        int carry = 0;
        int i = a.size() - 1, j = b.size() - 1;
        while (i >= 0 || j >= 0 || carry) {
            int sum = carry;
            if (i >= 0) {
              sum += a[i] - '0';
              i--;
            }
            if (j >= 0) {
              sum += b[j] - '0';
              j--;
            }
            carry = sum / 2;
            result += to_string(sum % 2);
        }

        reverse(result.begin(), result.end());
        return result;
    }
};
