class Solution {
public:
    string addBinary(string a, string b) {
        bool carry = false;
        int i = a.length() - 1, j = b.length()-1;
        string res(max(i,j)+1, '0');
        while(i >= 0 || j >= 0) {
            int cur = (i >= 0? a[i--]-'0' : 0) + (j >= 0 ? b[j--]-'0' : 0 ) + carry;
            res[max(i,j)+1] = (cur % 2) + '0';
            carry = (cur > 1);
        }
        if (carry) 
            res = "1" + res;
        return res;
    }
};

