var addBinary = function(a, b) {
    let aLen = a.length - 1;
    let bLen = b.length - 1;

    let binaryCount = "";
    let hasCarry = false;

    while(aLen >= 0 || bLen >= 0) {
        let num1 = (aLen >= 0 ? Number.parseInt(a[aLen]) : 0);
        let num2 = (bLen >= 0 ? Number.parseInt(b[bLen]) : 0);

        let value = hasCarry ? 1 ^ (num1 ^ num2) : (num1 ^ num2);

