class Solution:
    def addBinary(self, a: str, b: str) -> str:
        answer = ""
        carry = 0
        len_diff = abs(len(a)-len(b))
        if len(a) >= len(b):
            b = "0" * len_diff + b
        else:
            a = "0" * len_diff + a

        for i,j in zip(reversed(a), reversed(b)):
            summation = int(i) + int(j) + carry

            carry = summation // 2
            answer += str(summation % 2)

        if carry == 1:
            answer += "1"
        return answer[::-1]


