class Solution {
    public int flipLights(int n, int presses) {
        int statuses = 0;
        if(n==1 && presses > 0){
            statuses = n + 1;
        }
        else if(presses==1 && n < 4){
            statuses = n + presses;
        }
        else if((n == 2 && presses > 0) || (presses == 1 && n >= 4)){
            statuses = 4;
        }
        else if(presses == 2 && n > presses){
            statuses = 7;
        }
        else if(n*presses > 8){
            statuses = 8;
        }
        else if(n*presses < 8 && n > presses){
            statuses = n*presses + 1;
        }
        else if(n*presses <= 8 && n <= presses){
            statuses = 4;
        }
        else{
            return statuses = 1;
        }
        return statuses;
    }
}
