/**
 * Time Complexity: O(1)
 * Space Complexity: O(1)
 */
class Solution {
 public:
  int flipLights(const int n, const int presses) {
    if (presses == 0) {
      /**
       * all bulbs are turned on
       */
      return 1;
    }
    if (n == 1) {
      /**
       * the only bulb is turned on or off, since any button can be pressed at least once
       */
      return 2;
    }
    if (n == 2) {
      /**
       * 1. if `presses` is `1`, pressing button 3 and pressing button 4 have the same effect,
       *    so the number of different possible statuses after performing one button press is 3,
       *    that is i) press the button 1, ii) press the button 2, iii) press the button 3
       * 2. if `presses` is `2`, all 4 different possible statuses can be achieved finally.
       */
      return presses == 1 ? 3 : 4;
    }
    if (presses == 1) {
      /**
       * 1. press button 1 once
       * 2. press button 2 once
       * 3. press button 3 once
       * 4. press button 4 once
       */
      return 4;
    }
    /**
     * there are 8 possible statuses
     * 1. all bulbs are turned on
     * 2. press button 1
     * 3. press button 2
     * 4. press button 3
     * 5. press button 4
     * 6. press button 1 and 4
     * 7. press button 2 and 4
     * 8. press button 3 and 4
     *
     * a) if `presses` is 2, all 8 possible statuses except `5` can be achieved
     * b) if `presses` is greater than 2, all 8 possible statuses can be achieved
     */
    return presses == 2 ? 7 : 8;
  }
};
```

