var flipLights = function(n, m) {
    if (m === 0)    return 1


    const sets = {
        NONE: '',
        A: 'A',
        B: 'B',
        C: 'C',
        D: 'D',
    }
    if (n === 1) {
        sets.B = sets.A
    }
    if (n <= 2) {
        sets.D = sets.B
    }


    function getSet(...list) {
        let set = new Set()
        for (const ch of list) {
            if (set.has(ch))    set.delete(ch)
            else set.add(ch)
        }

        const chs = [...set]
        chs.sort()

        return chs.join('')
    }


    const results = []
    results[0] = new Set([sets.NONE])
    results[1] = new Set([sets.A, sets.B, sets.C, sets.D])
