class Solution {
public:
    int findNumberOfLIS(vector<int>& nums) {
        // vector dp stores length of lonegst inc subseq ending with nums[i]
        // vector cnt stores the number of subsequences of length dp[i]
        vector<int>dp(nums.size(),1),cnt(nums.size(),1);
        int mxLen=1,res=0;
        for(int i = 0; i < nums.size(); i++){

            for(int j = 0; j < i; j++){

                // if nums[j] < nums[i] we are sure that we can append nums[i] 
                if(nums[i] > nums[j]){

                    // append nums[i] only if length is incresed
                    if(dp[j]+1 > dp[i]){
                    dp[i] = dp[j]+1;
                    cnt[i] = cnt[j];
                    }
                    // if appending nums[i] after nums[j] gives length equal to dp[i] -> means there exists more number of ways to design subseq of this length so adding them to cnt[i]
                    else if(dp[j]+1 == dp[i])
                        cnt[i] += cnt[j];
                    }
            }
            // to store maxLen
            mxLen = max(mxLen, dp[i]);
        }
        //counting total number of ways to design subseq of length mxLen
        for(int i=0;i<dp.size();i++){
            if(dp[i] == mxLen)
                res += cnt[i];
        }
        return res;
    }
};
