class Solution:
    def findNumberOfLIS(self, nums: List[int]) -> int:
        record = [(1,1)]*len(nums)

        for i in range(1, len(nums)):
            for j in range(i):
                if nums[j]<nums[i]:
                    if record[j][0]+1 > record[i][0]:
                        record[i] = (record[j][0]+1, record[j][1])
                    elif record[j][0]+1 == record[i][0]:
                        record[i] = (record[i][0], record[j][1]+record[i][1])

        longest = max(length for length, _ in record)
        longest_count = 0
        for length, count in record:
            if length==longest:
                longest_count += count
        return longest_count

