class Solution {
public:
    int n, a[2001];
    pair<int, int> bit[2001];

    void Update(int i, int len, int cnt) {
        while (i <= n) {
            if (bit[i].first < len) {
                bit[i] = {len, cnt};
            }
            else if (bit[i].first == len) {
                bit[i].second += cnt;
            }
            i += i & -i;
        }
    }

    pair<int, int> Get(int i) {
        pair<int, int> ans = {0, 0};
        while (i > 0) {
            if (ans.first < bit[i].first) {
                ans = bit[i];
            }
            else if (bit[i].first == ans.first) {
                ans.second += bit[i].second;
            }
            i -= i & -i;
        }
        return ans;
    }

    int findNumberOfLIS(vector<int>& nums) {
        n = nums.size();
        for (int i = 1; i <= n; ++i) {
            a[i] = nums[i - 1];
        }
        sort(nums.begin(), nums.end());
        for (int i = 1; i <= n; ++i) {
            a[i] = lower_bound(nums.begin(), nums.end(), a[i]) - nums.begin() + 1;
        }
        for (int i = 1; i <= n; ++i) {
            auto [len, cnt] = Get(a[i] - 1);
            if (len == 0) {
                cnt = 1;
            }
            Update(a[i], ++len, cnt);
        }
        return Get(n).second;
    }
};
