class Solution {
public:
    int findNumberOfLIS(vector<int>& nums) {
        const int n = nums.size();
        if(n<2) return  n;
        vector<int> lens(n, 1); // lens[i]: length of LIS ends with nums[i]
        vector<int> cnts(n, 1); // cnts[i]: counts of LIS ends with nums[i]
        for(int i = 1; i < n; i++)    // i: right pointer
            for(int j = 0; j < i; j++) // j: left pointer
                if(nums[j] < nums[i]){ // left_item < right_item
                    if(lens[j] + 1 > lens[i]){//key point: if newly longer subsequence ending with nums[i] found, then
                        lens[i] = lens[j] + 1;
                        cnts[i] = cnts[j];
                    }else if(lens[j] + 1 == lens[i]){
                        cnts[i] += cnts[j];
                    }
                }
        int maxLen = *max_element(lens.begin(), lens.end());        
        int ans = 0;
        for(int i = 0; i < n; i++)
            if(lens[i] == maxLen)
                ans += cnts[i];
        return ans;
    }
};
