## RECURSION [TLE]
```
class Solution {
public:
    //LIS for each call will be longest increasing subsequence starting from "currIdx"
    //returns the {LISLen, LISCount}  [LISCount = no. of subsequences with len = LISLen]
    pair<int, int> solve(vector<int>&nums, int currIdx)
    {   
        int LISLen = 1, LISCount = 1;
        //============================================================================
        for (int i = currIdx + 1; i < nums.size(); i++)
        {
            if (nums[i] > nums[currIdx])
            {
                pair<int, int>nextLenCount = solve(nums, i);

                int currLen = 1 + nextLenCount.first; //1 + nextLen
                if (currLen > LISLen)
                {
                    LISLen = currLen;
                    LISCount = nextLenCount.second; //nextCount
                }
                else if (currLen == LISLen) 
                    LISCount = LISCount + nextLenCount.second;
            }
        }
        //=================================================================================
        return {LISLen, LISCount};
    }
    int findNumberOfLIS(vector<int>& nums) 
    {
        int n = nums.size(), maxLISLen = 1;

        unordered_map<int, int>mp;
        //===============================================================
        for (int start = 0; start < n; start++)  //LISLen and LISCount for subsequences starting at "start"
        {
            pair<int, int>lenCountPair  = solve(nums, start);
            int LISLen = lenCountPair.first, LISCount = lenCountPair.second;
            mp[LISLen] += LISCount;
            maxLISLen = max(maxLISLen, LISLen);
        }
        //=================================================================
        return mp[maxLISLen];

    }
};
