                    int currLen = 1 + length[i]; //1 + nextLen
                    if (currLen > LISLen)
                    {
                        LISLen = currLen;
                        LISCount = count[i]; //nextCount
                    }
                    else if (currLen == LISLen) 
                        LISCount = LISCount + count[i];
                }
            }
            //=============================================================
            length[start] = LISLen; 
            count[start] = LISCount;
            maxLISLen = max(maxLISLen, length[start]);
        }
        //========================================================================================
        int ans = 0;
        for (int i = 0; i < n; i++)
        {
            if (length[i] == maxLISLen) 
                ans += count[i];
        }
        return ans; 
    }
};
