class Solution {
public:
    typedef pair<int, int> p;

    int findNumberOfLIS(vector<int>& nums) 
    {
        int n = nums.size();
        vector<p> memo(n+1, make_pair(-1, -1));
        return helper(nums, memo, 0, -1).second;
    }
private:
    p helper(vector<int>& nums, vector<p>& memo, int idx, int prev)
    {
        if (idx == nums.size()) return make_pair(0, 1);
        if (memo[prev+1] != make_pair(-1, -1)) return memo[prev+1];
        p a = make_pair(0, 0), b = make_pair(0, 0);
        if (prev==-1 || nums[idx] > nums[prev]) 
        {
            a = helper(nums, memo, idx+1, idx);
            a.first++;
        }
        b = helper(nums, memo, idx+1, prev);
        if (a.first == b.first) return memo[prev+1] = make_pair(a.first, a.second+b.second);
        else if (a.first > b.first) return memo[prev+1] = a;
        return memo[prev+1] = b;
    }
};
