class Solution {
public:
    int findNumberOfLIS(vector<int>& nums) {

        int n = nums.size(), length = 0, ans = 0;

        // Now if we are given an array of size 0 , then no of subsequence formed is 0
        // with one element its 1

        vector< int > DP( n, 1 );
        vector< int > Count( n, 1 );

        // DP[ i ] -> Length of longest subsequence ending at ith index
        // Count[ i ] -> Count of no of subsequences ending at ith index

        for( int i=0; i<n ; i++ ){

            for( int j=0 ; j<i ; j++ ){

                if( nums[j] < nums[i] ){

                    // Now 2 situations arises
                    //   i) The i-th element gets added to the subsequence at index j

                    if( DP[ j ]+1 > DP[i] ){

                        DP[ i ] = 1 + DP[ j ];
                        Count[ i ] = Count[ j ];
                    }

                    //  ii) Increases the number of subsequences as the length of that longest subsequence already achieved for that element

                    else if( DP[ j ]+1 == DP[ i ] )
                        Count[ i ] += Count[j];
                }
            }

            length = max( length , DP[ i ] );
        }

        // count of all longest increasing subsequence that end with ith indexes
        for( int i=0 ; i<n ; i++ ){

            if( length==DP[i] )
                 ans += Count[ i ];
        }

        return ans;
    }
};

