class Solution {
public:
    int findNumberOfLIS(const vector<int>& nums) {
        if (nums.empty())
            return 0;

        int n = nums.size();

        vector<vector<int>> deck(n + 1);
        vector<vector<int>> path(n + 1);

        int max_so_far = 0;
        for (int i = 0; i < n; ++i) {
            // bsearch insertion point
            // sorted from l to r
            int l = 0, r = max_so_far;
            while (l < r) {
                int mid = l + (r - l) / 2;
                if (nums[i] > deck[mid].back()) {
                    l = mid + 1;
                } else {
                    r = mid;
                }
            }

            // bsearch number of options
            int options = 1;
            int row = l - 1; // closest row with elements not greater than nums[i]
            if (row >= 0) {
                int l1 = 0, r1 = deck[row].size();
                // sorted from r to l
                while (l1 < r1) {
                    int mid = l1 + (r1 - l1) / 2;
                    if (nums[i] > deck[row][mid]) {
                        r1 = mid;
                    } else {
                        l1 = mid + 1;
                    }
                }

                options = path[row].back();
                if (l1 != 0) // in a row was found element greater than nums[i]
                    options -= path[row][l1 - 1];
                //cout << nums[i] << ' ' << options << ' ' << l1 << endl;
            }

            deck[l].push_back(nums[i]);
            if (path[l].empty())
                path[l].push_back(options);
            else
                path[l].push_back(path[l].back() + options);

            if (l == max_so_far)
                max_so_far++;
        }

        return path[max_so_far - 1].back();
    }
};

