class Solution {
    fun findNumberOfLIS(A: IntArray): Int {
        var N = A.size
        var cnt = IntArray(N) { 1 }
        var dp = IntArray(N) { 1 }
        var (best, hi) = listOf(0, 0)
        for (j in 0 until N) {
            for (i in 0 until j) {
                if (A[i] < A[j]) {
                    if (dp[j] < 1 + dp[i]) {
                        dp[j] = 1 + dp[i]
                        cnt[j] = 0
                    }
                    if (dp[j] == 1 + dp[i])
                        cnt[j] += cnt[i]
                }
            }
            if (best < dp[j]) {
                best = dp[j]
                hi = 0
            }
            if (best == dp[j])
                hi += cnt[j]
        }
        return hi
    }
}
