```
class Solution {
public:
   static const int N=4*2*1e3;
    pair<int,int> seg[N];
    vector<int> arr;
    pair<int,int> helperUpdateMax(pair<int,int> a, pair<int,int> b)
    {
        if(a.first>b.first)
           return a;
        else if(a.first==b.first)
          {
              a.second+= b.second;
              return a;
          }
          return b;
    }
    void update(int i,int lo,int hi, int ind, pair<int,int> x)
    {
        if(ind<lo || ind>hi) return;
        if(lo==hi && lo==ind)
        {
            seg[i]=helperUpdateMax(seg[i],x);
            return;
        }
        int mid= (lo+hi)/2;
        update(2*i+1,lo,mid,ind,x);
        update(2*i+2,mid+1,hi,ind,x);
        seg[i]=helperUpdateMax(seg[2*i+1],seg[2*i+2]);

    }
    pair<int,int>  sum(int i,int lo,int hi , int l, int r)
    {
        if(l<=lo && r>=hi)
        {
            return seg[i];
        }
        if(r<lo || l>hi) return make_pair(0,0);
        int mid= (lo+hi)/2;
        return helperUpdateMax(sum(2*i+1,lo,mid,l,r),sum(2*i+2,mid+1,hi,l,r));
    }
    int findNumberOfLIS(vector<int>& nums) {
        int n=nums.size();

        vector<int> vec=nums;
        sort(vec.begin(),vec.end());
        map<int,int> mp;
        int ind=0;
        for(int i=0;i<n;i++)
        {
            if(mp.find(vec[i])==mp.end())
            {
                 mp[vec[i]]=ind++;
            }

        }
        for(int i=0;i<n;i++)
        {
            nums[i]=mp[nums[i]];
        }
        for(int i=0;i<4*ind;i++)
        {
            seg[i]=make_pair(0,0);
        }
        int mx=1;
         pair<int,int> dp[n];
        for(int i=0;i<n;i++)
        {
            if(nums[i]>0)
            {
             pair<int,int> x=sum(0,0,ind-1,0,nums[i]-1);
            dp[i]=x;
            dp[i].first++;
            if(dp[i].second==0) dp[i].second=1;

            }
            else
            {
                dp[i]={1,1};
            }
            mx=max(mx,dp[i].first);
            update(0,0,ind-1,nums[i],dp[i]);
        }
        int cnt=0;
        for(int i=0;i<n;i++)
        {
            if(dp[i].first==mx) cnt+=dp[i].second;
        }
        return cnt;

    }
};
