class Solution:
    freq = 0
    longest = 0
    def findNumberOfLIS(self, nums: List[int]) -> int:
        @cache
        def dfs(i, prev):
            # Base cases
            if i >= len(nums):
                return 0, 1
            # can only choose if s-increasing
            choose, choose_cnt = 0, 0
            if nums[i] > prev:
                choose, choose_cnt = dfs(i+1, nums[i])
                choose += 1
            ignore, ignore_cnt = dfs(i+1, prev)
            if ignore == choose:
                return choose, choose_cnt + ignore_cnt
            elif choose > ignore:
                return choose, choose_cnt
            return ignore, ignore_cnt

        ans, ans_cnt = dfs(0, -float('inf'))
        return ans_cnt



