class Solution(object):
    def findNumberOfLIS(self, nums):
        # Check if the input list 'nums' is empty
        if not nums:
            return 0

        # Initialize two arrays, dp1 and dp2, with all 1's to store the length and count of LIS respectively
        dp1, dp2 = [1] * len(nums), [1] * len(nums)

        # Initialize 'count' and 'maxval' variables to track the maximum length and count of LIS found so far
        count, maxval = 1, 1

        # Loop through the 'nums' list starting from the second element
        for i in range(1, len(nums)):
            # Compare the current element with all the previous elements (from 0 to i-1)
            for j in range(i):
                # If the current element is greater than the previous element, it can be part of the LIS
                if nums[i] > nums[j]:
                    # If the LIS ending at the current element (i) is longer than the LIS ending at the previous element (j),
                    # update the length and count of LIS for the current element (i)
                    if dp1[j] + 1 > dp1[i]:
                        dp1[i], dp2[i] = dp1[j] + 1, dp2[j]
                    # If the LIS ending at the current element (i) has the same length as the LIS ending at the previous element (j),
                    # add the count of LIS ending at the previous element (j) to the count of LIS for the current element (i)
                    elif dp1[j] + 1 == dp1[i]:
                        dp2[i] += dp2[j]

            # Update 'maxval' and 'count' if the length of LIS for the current element (i) is greater than or equal to 'maxval'
            if dp1[i] > maxval:
                maxval, count = dp1[i], dp2[i]
            elif dp1[i] == maxval:
                count += dp2[i]

        # Return the final count of longest increasing subsequences
        return count

