class Solution:
    def findNumberOfLIS(self, nums: List[int]) -> int:

        class BIT:
            # There are 2 binary index trees
            # one to store the longest increasing subsequence up until a given index
            # second to store the count of this lic at given index 
            def __init__(self, size) -> None:
                self.size = size + 1
                self.lic = [0] * self.size
                self.count = [0] * self.size

            def update(self, idx, lic, count):
                idx += 1
                while idx < self.size:
                    if self.lic[idx] == lic:
                        self.count[idx] += count
                    elif self.lic[idx] < lic:
                        self.lic[idx] = lic
                        self.count[idx] = count
                    idx += idx & (-idx)

            def query(self, idx):
                lic = 0
                count = 0
                idx += 1
                while idx > 0:
                    if self.lic[idx] == lic:
                        count += self.count[idx]
                    elif self.lic[idx] > lic:
                        lic = self.lic[idx]
                        count = self.count[idx]
                    idx -= idx & (-idx)
                return lic, count 

        def solve(nums):
            temp = sorted(set(nums))
            n = len(temp)
            bit = BIT(n)
            maxlength = 0
            total = 0
            for num in nums:
                idx = bisect.bisect_left(temp, num)
                # get the values up until this index
                lic, count = bit.query(idx - 1)
                # add the inclusion of this iterated value in its lic
                lic += 1
                if lic == 1:
                    count = 1
                if lic == maxlength:
                    total += count
                elif lic > maxlength:
                    maxlength = lic
                    total = count
                bit.update(idx, lic, count)
            return total

        return solve(nums)


