class Solution {
    public int findNumberOfLIS(int[] a) {
        int size = a.length;
        int[] dp = new int[size];
        int[] count = new int[size];
        Arrays.fill(dp , 1);
        Arrays.fill(count , 1);
        int maxLen = 0;

        for( int i=0 ; i<size ; i++ ){
            for( int prev=0 ; prev<i ; prev++ ){
                if(a[i] > a[prev]){
                    if(1 + dp[prev] > dp[i]){
                       dp[i] = 1 + dp[prev];
                       count[i] = count[prev]; // As LIS till i is now adding the LIS till prev. So no. of LIS of this length till i will become the no. of LIS at prev . Since count[prev] is holding the no. of LIS of length dp[prev] till prev index and we are just adding a[i] in our LIS till i. So count[i] becomes equal to count[prev]
                    }else if(1 + dp[prev] == dp[i]){  
                        count[i] += count[prev] ; // Since, no of LIS of this length "1 + dp[prev]" is already here at count[i]. Therefore count of LIS of this length increases by no of LIS of that length at count[prev].
                    }
                }
            }

            if(dp[i] > maxLen){
                maxLen = dp[i];
            }
        }

        int noOfLIS = 0;
        for( int i=0 ; i<size ; i++ ){
            if(dp[i] == maxLen) noOfLIS += count[i];
        }

        return noOfLIS;
    }
}
