class Solution {
    public int findNumberOfLIS(int[] nums) {
        int n=nums.length;
        int dp[]=new int[n];
        int cnt[]=new int[n]; /*For count array*/
        Arrays.fill(dp,1);
        Arrays.fill(cnt,1);
        int max=1;
        int res=0;
        for(int ind=0;ind<n;ind++){
            for(int prev=0;prev<ind;prev++){
                if(nums[ind]>nums[prev] && 1+dp[prev]>dp[ind]){
                    dp[ind]=1+dp[prev];
                    cnt[ind]=cnt[prev]; /*If its largest then inherit or copy the count
					                      from previous element*/
                }
                else if(nums[ind]>nums[prev] && 1+dp[prev]==dp[ind]){
                    cnt[ind]=cnt[ind]+cnt[prev]; /*If the lis length is same then count the 
					                               different occurences of same length by adding 
												   the current count and previous count so that 
												   we can know the number of occurences 
												   of same length. */
                }
            }
            if(dp[ind]>max){
                max=dp[ind];
            }
        }
        for(int i=0;i<n;i++){
            if(dp[i]==max) /*After finding the length of lis which is in max, we just traverse the dp array 
			                 and whichever element has same max length, their corresponding 
							 count from cnt will be added.*/ 
                res+=cnt[i];
        }
        return res;
    }
}
