    int findNumberOfLIS(vector<int>& nums) {
        int n = nums.size(), maxi = 1;  // maximum length of lis
        vector<int> dp(n,1);            // stores length of longest sequence till i-th position
        vector<int> cnt(n,1);           // stores count of longest sequence of length dp[ind]
        for(int ind = 0;ind<n;ind++){
            for(int j = 0;j<ind;j++){

                if(nums[j]<nums[ind]){
                    if(1+dp[j]>dp[ind]){    // strictly increasing
                        dp[ind] = 1+dp[j];
                        cnt[ind] = cnt[j];
                    }else if(1+dp[j]==dp[ind]){ // if there are more subsequences of same length ending at length dp[ind]
                        cnt[ind] += cnt[j];
                    }
                }
            }
            maxi = max(maxi,dp[ind]);
        }

        int nos = 0; // count all the LIS of length maxi
        for(int i = 0;i<n;i++){
            if(dp[i]==maxi) nos+=cnt[i];
        }
        return nos;
    }
};
