                elif length_2 > length_1 + 1:
                    memo[(prev_num, curr_i)] = length_2, count_2
                else:
                    memo[(prev_num, curr_i)] = length_2, count_1 + count_2

            else:
                memo[(prev_num, curr_i)] = helper(prev_num, curr_i + 1)
            return memo[(prev_num, curr_i)]

        l, c = helper()
