class Solution {

    public int findNumberOfLIS(int[] nums) {
        Segment root = new Segment((int) -1e6, (int) 1e6);
        for (int num : nums) {
            Sequence s = root.query(num - 1);
            root.update(num, new Sequence(s.length + 1, s.count));
        }
        return root.sequence.count;
    }

    private static class Segment {
        final int left;
        final int middle;
        final int right;

        Segment leftChild;
        Segment rightChild;

        Sequence sequence = Sequence.EMPTY;

        Segment(int left, int right) {
            this.left = left;
            this.middle = left + (right - left) / 2;
            this.right = right;
        }

        void update(int value, Sequence s) {
            this.sequence = this.sequence.merge(s);

            if (this.left == this.middle) {
                return;
            }

            if (value < this.middle) {
                if (this.leftChild == null) {
                    this.leftChild = new Segment(this.left, this.middle);
                }
                this.leftChild.update(value, s);
            } else {
                if (this.rightChild == null) {
                    this.rightChild = new Segment(this.middle, this.right);
                }
                this.rightChild.update(value, s);
            }
        }

        Sequence query(int value) {
            if (this.left == this.middle) {
                return this.sequence;
            }

            if (value < this.middle) {
                return this.leftChild != null ? this.leftChild.query(value) : Sequence.EMPTY;

            } else {
                Sequence left = this.leftChild != null ? this.leftChild.sequence : Sequence.EMPTY;
                Sequence right = this.rightChild != null ? this.rightChild.query(value) : Sequence.EMPTY;
                return left.merge(right);
            }
        }
    }

    private static class Sequence {
        final int length;
        final int count;

        Sequence(int length, int count) {
            this.length = length;
            this.count = count;
        }

        Sequence merge(Sequence other) {
            if (other.length > this.length) {
                return other;
            }
            if (other.length < this.length) {
                return this;
            }
            return new Sequence(length, this.count + other.count);
        }

        static final Sequence EMPTY = new Sequence(0, 1) {
            Sequence merge(Sequence other) {
                return other.length > this.length ? other : this;
            }
        };
    }
}
